// src/api/index.ts
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000', // або твій бекенд URL
  timeout: 10000,
});

export default api;


const API_BASE = 'http://localhost:5000';

export const predictSolubility = async (smiles: string) => {
  const response = await axios.post(`${API_BASE}/predict`, { smiles });
  return response.data;
};

export const fetchHistory = async () => {
  const response = await axios.get(`${API_BASE}/history`);
  return response.data;
};

export const trainModel = async (file: File) => {
  const formData = new FormData();
  formData.append('file', file);
  const response = await axios.post(`${API_BASE}/train`, formData, {
    headers: { 'Content-Type': 'multipart/form-data' },
  });
  return response.data;
};

export const fetchMetrics = async () => {
  const response = await axios.get(`${API_BASE}/metrics`);
  return response.data;
};

export const fetchImportance = async () => {
  const response = await axios.get(`${API_BASE}/importance`);
  return response.data;
};

export const fetchDistribution = async () => {
  const response = await axios.get(`${API_BASE}/distribution`);
  return response.data;
};

export async function getFeatureImportance() {
  const res = await axios.get(`${API_BASE}/feature-importancen`);
  return await res.data;
}

export async function getDistribution() {
  const res = await axios.get(`${API_BASE}/distribution`);
  return await res.data;
}

export async function getCheckedMolecules() {
  const res = await axios.get(`${API_BASE}/checked-molecules`);
  return res.data;
}
