import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid } from 'recharts';
import { getFeatureImportance, getDistribution } from '../api';


const tabsGroup1 = [
  'History of Versions',
  'Comparisons of Models',
  'General Metrics',
  'Chemical Classes'
];

const tabsGroup2 = [
  'Distribution',
  'Validation',
  'Checked Molecules'
];

const Analytics: React.FC = () => {
  const [activeGroup, setActiveGroup] = useState(1);
  const [activeTab, setActiveTab] = useState(0);
  const [featureImportance, setFeatureImportance] = useState<any[]>([]);
  const [distribution, setDistribution] = useState<any>(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      try {
        if (activeGroup === 1 && tabsGroup1[activeTab] === 'General Metrics') {
          const fi = await getFeatureImportance();
          setFeatureImportance(fi);
        } else if (activeGroup === 2 && tabsGroup2[activeTab] === 'Distribution') {
          const dist = await getDistribution();
          setDistribution(dist);
        }
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [activeGroup, activeTab]);

  const renderContent = () => {
    const tabName = activeGroup === 1 ? tabsGroup1[activeTab] : tabsGroup2[activeTab];

    if (loading) return <p>Loading...</p>;

    if (tabName === 'General Metrics') {
      return (
        <div>
          <h3>Feature Importance</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={featureImportance}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="feature" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="importance" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      );
    }

    if (tabName === 'Distribution' && distribution) {
      return (
        <div>
          <h3>Distribution of Descriptors</h3>
          <ul>
            {Object.entries(distribution).map(([key, val]: any) => (
              <li key={key}>
                <strong>{key}</strong>: mean = {val.mean}, std = {val.std}
              </li>
            ))}
          </ul>
        </div>
      );
    }

    return <p>Графік для "{tabName}" ще не реалізований.</p>;
  };

  return (
    <div>
      <h2>Analytics</h2>
      <div style={{ marginBottom: 10 }}>
        <button onClick={() => { setActiveGroup(1); setActiveTab(0); }}>Group 1</button>
        <button onClick={() => { setActiveGroup(2); setActiveTab(0); }}>Group 2</button>
      </div>
      <div style={{ display: 'flex', gap: '10px', marginBottom: 20 }}>
        {(activeGroup === 1 ? tabsGroup1 : tabsGroup2).map((tab, idx) => (
          <button
            key={tab}
            onClick={() => setActiveTab(idx)}
            style={{ fontWeight: idx === activeTab ? 'bold' : 'normal' }}
          >
            {tab}
          </button>
        ))}
      </div>
      {renderContent()}
    </div>
  );
};

export default Analytics;
