import React, { useState, useEffect } from 'react';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid,
  LineChart, Line,
  PieChart, Pie, Cell,
} from 'recharts';

import {
  getFeatureImportance,
  getDistribution,
  getHistoryOfVersions,
  getModelComparisons,
  getCheckedMolecules,
} from '../api';

const tabsGroup1 = [
  'History of Versions',
  'Comparisons of Models',
  'General Metrics',
];

const tabsGroup2 = [
  'Distribution',
  'Checked Molecules'
];

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

const Analytics: React.FC = () => {
  const [activeGroup, setActiveGroup] = useState(1);
  const [activeTab, setActiveTab] = useState(0);
  const [loading, setLoading] = useState(false);

  // Стейти для даних різних вкладок
  const [featureImportance, setFeatureImportance] = useState<any[]>([]);
  const [distribution, setDistribution] = useState<any>(null);
  const [historyData, setHistoryData] = useState<any[]>([]);
  const [comparisonData, setComparisonData] = useState<any[]>([]);
  const [validationData, setValidationData] = useState<any[]>([]);
  const [checkedMoleculesData, setCheckedMoleculesData] = useState<any[]>([]);

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      try {
        if (activeGroup === 1) {
          switch (tabsGroup1[activeTab]) {
            case 'History of Versions':
              setHistoryData(await getHistoryOfVersions());
              break;
            case 'Comparisons of Models':
              setComparisonData(await getModelComparisons());
              break;
            case 'General Metrics':
              setFeatureImportance(await getFeatureImportance());
              break;
          }
        } else if (activeGroup === 2) {
          switch (tabsGroup2[activeTab]) {
            case 'Distribution':
              setDistribution(await getDistribution());
              break;
            case 'Validation':
              setValidationData(await getValidationData());
              break;
            case 'Checked Molecules':
              setCheckedMoleculesData(await getCheckedMolecules());
              break;
          }
        }
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [activeGroup, activeTab]);

  const renderContent = () => {
    const tabName = activeGroup === 1 ? tabsGroup1[activeTab] : tabsGroup2[activeTab];
    if (loading) return <p>Loading...</p>;

    switch (tabName) {
      case 'History of Versions':
        return (
          <table border={1} cellPadding={5}>
            <thead>
              <tr>
                <th>Version</th>
                <th>Date</th>
                <th>Accuracy</th>
              </tr>
            </thead>
            <tbody>
              {historyData.map((item, idx) => (
                <tr key={idx}>
                  <td>{item.version}</td>
                  <td>{item.date}</td>
                  <td>{(item.accuracy * 100).toFixed(1)}%</td>
                </tr>
              ))}
            </tbody>
          </table>
        );

      case 'Comparisons of Models':
        return (
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={comparisonData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="model" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="rmse" fill="#82ca9d" />
            </BarChart>
          </ResponsiveContainer>
        );

      case 'General Metrics':
        return (
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={featureImportance}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="feature" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="importance" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        );

      

      case 'Distribution':
        if (!distribution) return <p>No distribution data</p>;
        return (
          <ul>
            {Object.entries(distribution).map(([key, val]: any) => (
              <li key={key}>
                <strong>{key}</strong>: mean = {val.mean.toFixed(3)}, std = {val.std.toFixed(3)}
              </li>
            ))}
          </ul>
        );

      case 'Validation':
        return (
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={validationData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="epoch" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="trainLoss" stroke="#8884d8" name="Train Loss" />
              <Line type="monotone" dataKey="valLoss" stroke="#82ca9d" name="Validation Loss" />
            </LineChart>
          </ResponsiveContainer>
        );

      case 'Checked Molecules':
        return (
          <table border={1} cellPadding={5}>
            <thead>
              <tr>
                <th>ID</th>
                <th>SMILES</th>
                <th>LogS</th>
              </tr>
            </thead>
            <tbody>
              {checkedMoleculesData.map((item, idx) => (
                <tr key={idx}>
                  <td>{item.id}</td>
                  <td>{item.smiles}</td>
                  <td>{item.logS}</td>
                </tr>
              ))}
            </tbody>
          </table>
        );

      default:
        return <p>Графік для "{tabName}" ще не реалізований.</p>;
    }
  };

  return (
    <div>
      <h2>Analytics</h2>
      <div style={{ marginBottom: 10 }}>
        <button onClick={() => { setActiveGroup(1); setActiveTab(0); }}>Group 1</button>
        <button onClick={() => { setActiveGroup(2); setActiveTab(0); }}>Group 2</button>
      </div>
      <div style={{ display: 'flex', gap: '10px', marginBottom: 20 }}>
        {(activeGroup === 1 ? tabsGroup1 : tabsGroup2).map((tab, idx) => (
          <button
            key={tab}
            onClick={() => setActiveTab(idx)}
            style={{ fontWeight: idx === activeTab ? 'bold' : 'normal' }}
          >
            {tab}
          </button>
        ))}
      </div>
      {renderContent()}
    </div>
  );
};

export default Analytics;
